function buildView() {
    "Potato" === randomVegetable && ($(".vegetable").html('<img src="images/potato.svg" class="animated zoomIn" alt="Potato or Tomato?">'), $("button").addClass("potatoBrown"));
    "Tomato" === randomVegetable && ($(".vegetable").html('<img src="images/tomato.svg" class="animated zoomIn" alt="Potato or Tomato?">'), $("button").addClass("tomatoRed"));
}

function runAnswer() {
    $("button").on("click", function() {
        var t = $(this).val();
        if (t === randomVegetable) {
            $(".result").fadeIn(650);
            $("#confetti").show();
            $(".result__content").html(`
                <p>Yup, that's a ${t}!</p>
                <p>You'd be surprised by how many have guessed wrong!</p>
                <a class="alt--button" href="javascript:location.reload();" data-ajax="false" onclick="ga('send', 'event', 'Game', 'Action', 'Replay');">
                    <span class="fa fa-refresh"></span>&nbsp;&nbsp;Play Again
                </a>
            `);
            $(".popup").click(function(t) {
                var a = 575,
                    e = 400,
                    n = ($(window).width() - a) / 2,
                    o = ($(window).height() - e) / 2,
                    s = "status=1,width=" + a + ",height=" + e + ",top=" + o + ",left=" + n;
                return window.open(this.href, "Share Potato or Tomato", s), !1;
            });
        } else {
            $(".vegetable img").removeClass("zoomIn").addClass("shake").on("animationend webkitAnimationEnd oAnimationEnd mozAnimationEnd msAnimationEnd", function() {
                $(this).removeClass("shake");
            });
            $(".result").fadeIn(650);
            $(".result__content").html('<div class="incorrect"><span class="animated fadeIn">no</span><span class="animated fadeIn">no</span><span class="animated fadeIn">no</span></div>');
            $(".incorrect span:last-child").on("animationend webkitAnimationEnd oAnimationEnd mozAnimationEnd msAnimationEnd", function() {
                $(".result").fadeOut(650);
            });
        }
    });
}

var vegetables = ["Potato", "Tomato"],
    randomVegetable = vegetables[Math.floor(Math.random() * vegetables.length)];

$(document).ready(function() {
    $(".popup").click(function(t) {
        var a = 575,
            e = 400,
            n = ($(window).width() - a) / 2,
            o = ($(window).height() - e) / 2,
            s = "status=1,width=" + a + ",height=" + e + ",top=" + o + ",left=" + n;
        return window.open(this.href, "Share Potato or Tomato", s), !1;
    });
});

buildView();
runAnswer();